INSERT INTO `oc_prefix_product_description` (`product_id`, `language_id`, `name`, `description`, `description_mini`, `meta_description`, `meta_keyword`, `seo_title`, `seo_h1`, `tag`) VALUES
(1, 1, 'BASIC 1000', 'Накопительные емкостные водонагреватели с одним змеевиком "BASIC", напольные,<br />\nспроектированы для удовлетворения базовых требований.<br />\nОни обеспечивают легкую и в достаточном объеме подачу горячей воды всем потребителям.<br />\nНагреватели могут подсоединяться к системам центрального отопления и устанавливаться с<br />\nсистемами дополнительной интеграции. Данные изделия идеально подходят для<br />\nпромышленного применения, а также использования в инженерных сетях общего пользования.<br />\n·  Фронтальный люк диаметром 134 мм для инспектирования и монтажа электрического<br />\nнабора.<br />\n·  Стальной резервуар с покрытием, эмалированный методом полива (850°С).<br />\n·  Фланец на верхнем торце диаметром 134 мм в комплекте с контрфланцем и трубкой<br />\nдатчика.<br />\n·  Магниевый анод для защиты от коррозии в комплекте.<br />\n·  Гидравлические соединения, расположенные на задней стороне изделия.<br />\n·  Изоляция: мод. 150-600 - жесткий пенополиуретан 50 или 70 мм, мод. 800-2000 - мягкий<br />\nили жесткий полиуретан 100 мм.<br />\n<br />\nТехнические характеристики:<br />\n•	Площадь теплообменника - 2,4  м. кв. <br />\n•	Объем воды в теплообменнике - 15,1 м. куб.<br />\n•	Расход воды в теплообменнике - 2,6  м. куб./ч <br />\n•	Потребляемая мощность теплообменника - 60  кВт <br />\n•	Потеря напора  - 60 мбар <br />\n•	Производительность по ГВС, 80/60°C - 10/45°C - 1,5 м. куб./ч <br />\n•	Вес в сухом состоянии - 245  кг.<br />\n', '', '', '', '', '', ''),
